sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const questionType = event.context.item.value.type;

    var local_ans = api.state.assmtQuestionList;
    var ans_value;
    var ans_resp;
    var sys_id = event.context.item.value.sys_id;

    //find answer in JSON and update
    var indexItemClicked = event.context.item.index;
    var qtn_selected = local_ans[0].questionList[indexItemClicked];

    switch(questionType){
        case "choice":
        ans_value = event.payload.item.value;
        ans_resp = event.payload.item.id;
        break;

        case "yes_no":
        ans_resp = event.payload.value;
        if (ans_resp == "yes"){
            ans_value = qtn_selected.points;
        } else {
            ans_value = 0;
        }
        break;

        default:
    }

qtn_selected.response = ans_resp;
qtn_selected.value = ans_value;

api.setState("assmtQuestionList", local_ans);

//console.log("****STATE****");
//console.log(JSON.stringify (api.state.assmtQuestionList, null, 4));
}
